\n                            	Tanggal transfer\r\n                            </td>\r\n                            <td style="width:20px;">\r\n                            	:\r\n                            </td>\r\n                            <td style="color:#000;">\r\n                            	[transfer_date]\r\n                            </td>\r\n                        </tr>\r\n                        <tr style="height:30px;">\r\n                        	<td style="width:150px;">\r\n                            	Bank tujuan\r\n                            </td>\r\n                            <td style="width:20px;">\r\n                            	:\r\n                            </td>\r\n                            <td style="color:#000;">\r\n                            	[destination_bank]\r\n                            </td>\r\n                        </tr>\r\n                        <tr style="height:30px;">\r\n                        	<td style="width:150px;">\r\n                            	Bank asal transfer\r\n                            </td>\r\n                            <td style="width:20px;">\r\n                            	:\r\n                            </td>\r\n                            <td style="color:#000;">\r\n                            	[bank_name]\r\n                            </td>\r\n                        </tr>\r\n                        <tr style="height:30px;">\r\n                        	<td style="width:150px;">\r\n                            	Jumlah transfer\r\n                            </td>\r\n                            <td style="width:20px;">\r\n                            	:\r\n                            </td>\r\n                            <td style="color:#000;">\r\n                            	[amount]\r\n                            </td>\r\n                        </tr>\r\n                        <tr style="height:30px;">\r\n                        	<td style="width:150px;">\r\n                            	Nama 