(7, 'payment_success', '[company_brand_name] - Pembayaran Terverifikasi ([order_no])', '[customer_email]', '[company_brand_name] &lt;[customer_email] &gt;', '<!DOCTYPE html>\r\n<html>\r\n    <head>\r\n        <meta name="viewport" content="width=device-width" />\r\n        <meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />\r\n        <title>DIVERTUNE</title>\r\n		<style>\r\n			/* -------------------------------------\r\n			GLOBAL RESETS\r\n			------------------------------------- */\r\n			\r\n			img\r\n			{\r\n				border: none;\r\n				-ms-interpolation-mode: bicubic;\r\n				max-width: 100%;\r\n				text-decoration:none;\r\n				outline:none;\r\n			}\r\n			\r\n			body\r\n			{\r\n				\r\n				font-family: Helvetica, arial, sans-serif;\r\n				-webkit-font-smoothing: antialiased;\r\n				font-size: 14px;\r\n				line-height: 1.4;\r\n				margin: 0;\r\n				padding: 0; \r\n				-ms-text-size-adjust: 100%;\r\n				-webkit-text-size-adjust: 100%;\r\n			}\r\n			\r\n			.body\r\n			{\r\n				width: 100%;\r\n				background-color: #ffffff;\r\n			}\r\n			\r\n			.logo\r\n			{\r\n				text-align:center;\r\n				padding-top:20px;\r\n				padding-bottom:20px;\r\n			}\r\n			\r\n			.spacer\r\n			{\r\n				background-color:#ededed;\r\n				height:1px;\r\n			}\r\n			\r\n			.content\r\n			{\r\n				padding:20px;\r\n				font-family: Helvetica, arial, sans-serif;\r\n				font-size: 14px;\r\n			}\r\n			\r\n			.Notif\r\n			{\r\n				font-family: Helvetica, arial, sans-serif;\r\n				font-size: 14px;\r\n				text-align:center;\r\n				vertical-align:middle;\r\n				color:#ffffff;\r\n				height:100px;\r\n				background-color:#666;\r\n				padding:10px;\r\n			}\r\n			\r\n			.ShoppingCart\r\n			{\r\n				font-size: 14px;\r\n				width:100%;\r\n				border-collapse:collapse;\r\n				color:#666766;\r\n			}\r\n			\r\n			.footer\r\n			{\r\n				width:100%;\r\n				display:inline-block;\r\n				float:left;\r\n				background-color:#666; \r\n				color:#FFF; \r\n				text-align:center;\r\n				vertical-align:middle;